import logo from './logo.svg';
import './App.css';
import getMoviesBySearchTerm from './utils.js';
import React, { useState, useEffect } from 'react';
import getMovieDetailsById from './utils.js';

function App() {

  const [searchInput, setSearchInput] = useState("");

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <input type="text" placeholder="Search" onChange={e => setSearchInput(e.target.value)} />
        <button onClick={getMoviesBySearchTerm(searchInput)}>Submit</button>
      </header>
    </div>
  );
}

export default App;
