import './App.css';
import * as utilities from './utils.js'
import React, { useState } from 'react';

/*
function MovieCard(movie) {
  return (
    <div>
      <img src="`${movie.Poster}`" alt="movie_poster" />
      <h2>`${movie.Title}`</h2>
      <p>`${movie.Type}`</p>
    </div>
  );
}

function MovieDetails(movie){
  return (
    <div>
      <img src="`${movie.Poster}`" alt="movie_poster" />
      <h2>`${movie.Title}`</h2>
      <p>`${movie.Rated}`</p>
      <p>`${movie.Runtime}`</p>
      <p>`${movie.Genre}`</p>
      <h3>Plot</h3>
      <p>`${movie.Plot}`</p>
      <h3>Actors</h3>
      <p>`${movie.Actors}`</p>
    </div>
  );
}
*/

function App() {

  // takes and stores user input using React Hooks
  const [searchInput, setSearchInput] = useState("");
  const [identificationInput, setIdentificationInput] = useState("");

  function searchSubmit(input){
    utilities.getMoviesBySearchTerm(input);
  }

  function IDSubmit(input){
    utilities.getMovieDetailsById(input);
  }

  return (
    <div className="App">
      <header className="App-header">
        {/* App Logo */}
        <img src="https://www.clipartmax.com/png/small/8-89896_film-icon-white-png.png" alt="W3Schools.com" />

        {/* App Name */}
        <h1>
          Movie Search
        </h1>

        {/* First user input; searches movies by title */}
        <label>Search by Title:</label>
        <input type="text" placeholder="Title" onChange={e => setSearchInput(e.target.value)} />
        <button onClick={() => searchSubmit(searchInput)}>Submit</button>

        {/* Second user input; searches specific movie by IMDb ID */}
        <label>Search by IMDb ID:</label>
        <input type="text" placeholder="ID" onChange={e => setIdentificationInput(e.target.value)} />
        <button onClick={() => IDSubmit(identificationInput)}>Submit</button>

        <label>–Output–</label>
        <div id="output"></div>
      </header>
    </div>
  );
}

export default App;
