import './App.css';
import * as utilities from './utils.js'
import React, { useState } from 'react';

function App() {

  const [searchInput, setSearchInput] = useState("");
  const [identificationInput, setIdentificationInput] = useState("");

  const [searchOutput, setSearchOutput] = useState([]);
  const [identificationOutput, setIdentificationOutput] = useState([]);

  return (
    <div className="App">
      <header className="App-header">
        <img src="https://www.clipartmax.com/png/small/8-89896_film-icon-white-png.png" alt="W3Schools.com" />
        <h1>
          Movie Search
        </h1>
        <label>Search by Title:</label>
        <input type="text" placeholder="Title" onChange={e => setSearchInput(e.target.value)} />
        <button onClick={() => setSearchOutput(oldArray => [...oldArray, utilities.getMoviesBySearchTerm(searchInput)])}>Submit</button>

        <label>Search by ID:</label>
        <input type="text" placeholder="ID" onChange={e => setIdentificationInput(e.target.value)} />
        <button onClick={() => setIdentificationOutput(utilities.getMovieDetailsById(identificationInput))}>Submit</button>
      </header>

      <p>
        {searchOutput.map( e => <div key={e.Title}>{ e.Title }</div> )}
      </p>

    </div>
  );
}

export default App;
